import styled from 'styled-components';

export const StyledReviewForm = styled.form`
    padding: 15px 20px;
`;

export const StyledReviewFormTextarea = styled.textarea`
    font-family: Arial, Helvetica, sans-serif;
    font-size: 16px;
    border-radius: 4px;
    padding: 10px;
    width: calc(100% - 22px);
`;
